INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.01', 'Auditer une communication numérique', 'Analyser un site web ou la présence en ligne d’une marque ou d’une organisation et établir différents rapports d’audit.', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.02', 'Concevoir une recommandation de communication numérique', 'Construire une recommandation de communication pour la sortie ou le repositionnement d''un produit ou d’un service.', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.03', 'Produire les éléments d''une communication visuelle', 'Créer les éléments graphiques et visuels pour une campagne de communication (un événement, la sortie d’un produit, le positionnement d’une marque…) et justifier ses choix esthétiques.', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.04', 'Produire un contenu audio et vidéo', 'Concevoir et produire un contenu audiovisuel dans une perspective de vulgarisation ou d’argumentation.', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.05', 'Produire un site Web', 'Concevoir et produire un contenu audiovisuel dans une perspective de vulgarisation ou d’argumentation', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ1.06', 'Gérer un projet de communication numérique', 'Mettre en place la gestion de projet pour proposer une recommandation de communication numérique.', 1, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ2.01', 'Explorer les usages du numérique', 'Concevoir, organiser et réaliser des entretiens utilisateur pour comprendre et mesurer les attentes des utilisateurs d’un site web, d’un service en ligne ou d’un produit multimédia.', 2, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ2.02', 'Concevoir un produit ou un service et sa communication', 'Projet intégrateur de fin de première année : Concevoir un produit ou un service et déployer sa communication. Cette communication peut inclure par exemple : un site Web de présentation du produit, une campagne de communication sur les réseaux sociaux, un événement, un site d’agence...', 2, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ2.03', 'Concevoir un site web avec une source de données', 'Développer un site web, exploitant une base de données et permettant quelques interactions simples pour le client (liste, recherche, ajout, ...). Le site proposera une mise en page respectant l’accessibilité et le W3C, mais aussi des interactions.', 2, 'SAE');
INSERT INTO sae203.sae_enseignement (id_enseignement, nom, description, semestre, type) VALUES ('SAÉ2.04', 'Construire sa présence en ligne', 'Étudier, analyser et construire sa présence professionnelle en ligne. Mettre en ligne ses productions personnelles, sur des sites dédiés ou en développant son site personnel.', 2, 'SAE');
